import React, { useState, useEffect } from "react";
import jsyaml from "js-yaml";
import konumlarYaml from "../konumlar.yaml";
import { Button } from "react-bootstrap";
import ROSLIB from "roslib";

const Konumlar = () => {
  const [konumlar, setKonumlar] = useState([]);
  const [ros, setRos] = useState(null);

  useEffect(() => {
    const initROS = () => {
      const ros = new ROSLIB.Ros({
        url: "ws://localhost:9090", // ROS köprüsünün adresini buraya ekleyin
      });

      ros.on("connection", () => {
        console.log("ROS bağlantısı kuruldu");
      });

      ros.on("error", (error) => {
        console.error("ROS hatası:", error);
      });

      ros.on("close", () => {
        console.log("ROS bağlantısı kapatıldı");
      });

      setRos(ros);
    };

    initROS();
  }, []);

  useEffect(() => {
    if (ros) {
      const fetchData = async () => {
        try {
          const response = await fetch(konumlarYaml);
          const data = await response.text();
          const parsedData = jsyaml.load(data);
          setKonumlar(parsedData);
        } catch (error) {
          console.error("Error fetching YAML data:", error);
        }
      };

      fetchData();
    }
  }, [ros]);

  const handleGoToKonum = (konum) => {
    if (ros) {
      // Bu kısımda ROS mesajını yayınlama işlemini gerçekleştirebilirsiniz
      // Örneğin, MoveBaseActionGoal mesajını kullanarak hedef konuma gitmek için bir hareket emri yayınlayabilirsiniz

      const goal = new ROSLIB.Message({
        goal: {
          target_pose: {
            header: {
              frame_id: "map",
            },
            pose: {
              position: {
                x: 1.0, // Hedef konumun x koordinatını buraya ekleyin
                y: 2.0, // Hedef konumun y koordinatını buraya ekleyin
              },
              orientation: {
                w: 1.0,
              },
            },
          },
        },
      });

      // Hareket emrini yayınla
      const pub = new ROSLIB.Topic({
        ros: ros,
        name: "/move_base/goal",
        messageType: "move_base_msgs/MoveBaseActionGoal",
      });
      pub.publish(goal);

      console.log("Go to konum:", konum);
    }
  };

  return (
    <div>
      <h1>Konumlar</h1>
      <ul>
        {konumlar.map((konum, index) => (
          <Button key={index} onClick={() => handleGoToKonum(konum)}>
            {konum}
          </Button>
        ))}
      </ul>
    </div>
  );
};

export default Konumlar;
