  /**
   * @method quartOut
   * @param {Number} t
   * @static
   * @return {Number}
   **/
  Ease.quartOut = Ease.getPowOut(4);
  /**
   * @method quartInOut
   * @param {Number} t
   * @static
   * @return {Number}
   **/
  Ease.quartInOut = Ease.getPowInOut(4);

  /**
   * @method quintIn
   * @param {Number} t
   * @static
   * @return {Number}
   **/
