  /**
   * @method quintOut
   * @param {Number} t
   * @static
   * @return {Number}
   **/
  Ease.quintOut = Ease.getPowOut(5);
  /**
   * @method quintInOut
   * @param {Number} t
   * @static
   * @return {Number}
   **/
  Ease.quintInOut = Ease.getPowInOut(5);

