import React, { useState, useEffect } from "react";
import jsyaml from "js-yaml";
import konumlarYaml from "../konumlar.yaml";
import { Button } from "react-bootstrap";
import ROSLIB from "roslib";
import ROS2D from './vendor/ros2d.js'
import { IntType } from "three";

const Konumlar = () => {
  const [konumlar, setKonumlar] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(konumlarYaml);
        const data = await response.text();
        const parsedData = jsyaml.load(data);
        setKonumlar(parsedData);
      } catch (error) {
        console.error("Error fetching YAML data:", error);
      }
    };

    fetchData();
  }, []);

  const handleGoToKonum = (konum) => {
    // Bu kısımda ROS mesajını yayınlama işlemini gerçekleştirebilirsiniz
    // Örneğin, ROSLIB kütüphanesini kullanarak ROS ile iletişim kurabilirsiniz

    // Örnek ROS mesajı oluşturma
    const goalMsg = new ROSLIB.Message({
      goal: {
        target_pose: {
          header: {
            frame_id: "map"
          },
          pose: {
            position: {
              x: 1.0, // Konumun x koordinatını buraya ekleyin
              y: 2.0 // Konumun y koordinatını buraya ekleyin
            },
            orientation: {
              w: 1.0
            }
          }
        }
      }
    });

     const pub = new ROSLIB.Topic({
       ros: ros,
       name: "/move_base/goal",
       messageType: "move_base_msgs/MoveBaseActionGoal"
     });
     pub.publish(goalMsg);

    console.log("Go to konum:", konum);
  };

  return (
    <div>
      <h1>Konumlar</h1>
      <ul>
        {konumlar.map((konum, index) => (
          <Button key={index} onClick={() => handleGoToKonum(konum)}>
            {konum}
          </Button>
        ))}
      </ul>
    </div>
  );
};

export default Konumlar;
